import React from "react";
import Back from "./assessts/bgc3.jpeg"; // Corrected the assets folder name

export default function SectionOne() {
  return (
    <div
      className="relative isolate px-6 pt-20 lg:px-8 text-center bg-white mt-20 overflow-hidden"
      style={{
        backgroundImage: `url(${Back})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        height: "70vh",
      }}
    >
      <h1 className="text-3xl font-bold tracking-tight text-white sm:text-5xl shadow-lg glow">
        Experience Authentic Indian Flavors
      </h1>
      <h2 className="mt-6 text-lg leading-8 text-white shadow-md glow">
        Delightful curries and dishes crafted with passion and tradition.
      </h2>
      <div className="mt-8 flex justify-center gap-x-4">
        <a
          href="#"
          className="rounded-md bg-orange-600 px-4 py-2 text-sm font-semibold text-white shadow-sm hover:bg-orange-500 transition duration-200 glow"
        >
          Order
        </a>
        <a
          href="#"
          className="rounded-md border border-gray-300 px-4 py-2 text-sm font-semibold text-white hover:bg-gray-100 transition duration-200"
        >
          Menu
        </a>
      </div>

      {/* Custom CSS for glowing animation and text highlight */}
      <style jsx global>{`
        @keyframes glow {
          0% {
            box-shadow: 0 0 5px rgba(0, 123, 255, 0.7);
          }
          50% {
            box-shadow: 0 0 20px rgba(0, 255, 123, 1);
          }
          100% {
            box-shadow: 0 0 5px rgba(0, 123, 255, 0.7);
          }
        }
        .glow {
          animation: glow 1.5s infinite alternate;
        }

        /* Text shadow for better visibility */
        h1,
        h2 {
          text-shadow: 0 2px 4px rgba(0, 0, 0, 0.7);
        }

        /* Media query for responsive adjustments */
        @media (max-width: 1024px) {
          .relative {
            height: 50vh; // Adjust height for iPad
            overflow: hidden; // Prevent overflow
          }
          h1 {
            margin-top: 5vh; // Adjust margin for better positioning
            font-size: 2.5rem; // Smaller size for mobile/tablet
          }
          h2 {
            margin-top: 1vh; // Adjust spacing
            font-size: 1.25rem; // Smaller size for mobile/tablet
          }
          a {
            font-size: 0.875rem; // Smaller button text for mobile/tablet
          }
        }

        @media (min-width: 1025px) {
          h1 {
            margin-top: 10vh; // Increase top margin for laptop
            font-size: 4rem; // Increase size for laptop
          }
          h2 {
            margin-top: 2vh; // Increase spacing for laptop
            font-size: 1.5rem; // Increase size for laptop
          }
          a {
            font-size: 1rem; // Increase button text for laptop
          }
        }
      `}</style>
    </div>
  );
}
