import React from "react";
import "./PhotoGrid.css"; // Ensure this file exists

const photos = [
  {
    id: 1,
    src: "https://images.pexels.com/photos/1640774/pexels-photo-1640774.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940",
    alt: "Butter Chicken",
  },
  {
    id: 2,
    src: "https://images.pexels.com/photos/1170890/pexels-photo-1170890.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940",
    alt: "Palak Paneer",
  },
  {
    id: 3,
    src: "https://images.pexels.com/photos/1640773/pexels-photo-1640773.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940",
    alt: "Aloo Gobi",
  },
  {
    id: 4,
    src: "https://images.pexels.com/photos/3406792/pexels-photo-3406792.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940",
    alt: "Chole Bhature",
  },
  {
    id: 5,
    src: "https://images.pexels.com/photos/2648686/pexels-photo-2648686.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940",
    alt: "Paneer Tikka",
  },
  // Add more images as needed
];

const SectionTwo = () => {
  return (
    <div className="relative overflow-hidden my-10 px-4">
      {" "}
      {/* Added vertical margin and horizontal padding */}
      <h2 className="text-3xl font-bold text-center mb-10">
        Explore Our Culinary Delights
      </h2>{" "}
      {/* Increased bottom margin */}
      <div className="flex animate-slide min-h-[250px] justify-center py-4">
        {" "}
        {/* Added vertical padding */}
        {photos.map((photo) => (
          <div
            key={photo.id}
            className="photo-card transform transition-transform duration-500 hover:scale-105 mx-4" // Increased horizontal margin for spacing
          >
            <img
              src={photo.src}
              alt={photo.alt}
              className="w-48 h-48 object-cover rounded-lg mb-3" // Added bottom margin to images
            />
            <div className="p-3">
              <h3 className="text-lg font-semibold">{photo.alt}</h3>
            </div>
          </div>
        ))}
        {/* Duplicate for infinite scroll effect */}
        {photos.map((photo) => (
          <div
            key={photo.id + 100} // Unique key
            className="photo-card transform transition-transform duration-500 hover:scale-105 mx-4" // Increased horizontal margin for spacing
          >
            <img
              src={photo.src}
              alt={photo.alt}
              className="w-48 h-48 object-cover rounded-lg mb-3" // Added bottom margin to images
            />
            <div className="p-3">
              <h3 className="text-lg font-semibold">{photo.alt}</h3>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SectionTwo;
